import React, { useEffect, useState } from 'react';
import api from '../services/api';

interface Fixture {
  id: number;
  round_number: number;
  match_number: number;
  player1: { id: number; name: string } | null;
  player2: { id: number; name: string } | null;
  winner: { id: number; name: string } | null;
}

const Fixtures: React.FC = () => {
  const [fixtures, setFixtures] = useState<Fixture[]>([]);
  const [loading, setLoading] = useState(false);
  const [fetchLoading, setFetchLoading] = useState(true);
  const [error, setError] = useState('');
  const [message, setMessage] = useState('');

  useEffect(() => {
    fetchFixtures();
  }, []);

  const fetchFixtures = async () => {
    setFetchLoading(true);
    try {
      const res = await api.get('/fixtures/fixtures/');
      if (res.data && !Array.isArray(res.data)) {
        console.error('Unexpected response format:', res.data);
        setFixtures([]);
        setError('Unexpected response format from server');
        return;
      }
      setFixtures(res.data || []);
      setError('');
    } catch (err) {
      console.error('Error fetching fixtures:', err);
      setFixtures([]);
      setError('Failed to load fixtures');
    } finally {
      setFetchLoading(false);
    }
  };

  const generateDefault = async () => {
    setLoading(true);
    setMessage('Generating fixtures...');
    try {
      const res = await api.post('/fixtures/generate_default/');
      setMessage('Fixtures generated for tournament ' + res.data.tournament_id);
      await fetchFixtures();
    } catch (err: any) {
      console.error(err);
      setMessage(err?.response?.data ? JSON.stringify(err.response.data) : 'Failed to generate fixtures');
    } finally {
      setLoading(false);
    }
  };

  const groupFixturesByRound = (fixtures: Fixture[]) => {
    const grouped: { [key: number]: Fixture[] } = {};
    fixtures.forEach(fixture => {
      if (!grouped[fixture.round_number]) {
        grouped[fixture.round_number] = [];
      }
      grouped[fixture.round_number].push(fixture);
    });
    return grouped;
  };

  const groupedFixtures = groupFixturesByRound(fixtures);

  return (
    <div className="p-4 fade-in">
      <div className="card">
        <div className="card-header">
          <h2 className="mb-0">üèÜ Tournament Fixtures</h2>
        </div>
        <div className="card-body">
          <div className="mb-4">
            <button
              onClick={generateDefault}
              disabled={loading}
              className="btn btn-secondary"
            >
              {loading ? 'Generating...' : 'üéØ Generate Fixtures (Auto)'}
            </button>
            {message && (
              <span className="ml-3" style={{ color: message.includes('Failed') ? 'var(--accent-primary)' : 'var(--accent-secondary)' }}>
                {message}
              </span>
            )}
          </div>

          {error && <div className="error">{error}</div>}

          {fetchLoading ? (
            <div className="loading">
              <div>Loading fixtures...</div>
            </div>
          ) : fixtures.length === 0 ? (
            <div className="text-center p-4">
              <div style={{ fontSize: '4rem', marginBottom: '1rem' }}>üèì</div>
              <h3>No fixtures yet</h3>
              <p className="text-muted">Generate fixtures to start the tournament bracket</p>
            </div>
          ) : (
            <div className="bracket-container">
              {Object.entries(groupedFixtures)
                .sort(([a], [b]) => Number(a) - Number(b))
                .map(([round, roundFixtures]) => (
                  <div key={round} className="round-section mb-4">
                    <h3 className="mb-3 court">
                      Round {round}
                      <span className="ml-2 text-muted">({roundFixtures.length} matches)</span>
                    </h3>
                    <div
                      style={{
                        display: 'grid',
                        gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))',
                        gap: '1rem'
                      }}
                    >
                      {roundFixtures.map((fixture) => (
                        <div
                          key={fixture.id}
                          className="card"
                          style={{
                            border: fixture.winner ? '2px solid var(--accent-secondary)' : '1px solid var(--border-color)'
                          }}
                        >
                          <div className="card-body">
                            <div className="text-center mb-3">
                              <span className="badge" style={{
                                background: 'var(--bg-accent)',
                                color: 'var(--text-primary)',
                                padding: '0.25rem 0.75rem',
                                borderRadius: '12px'
                              }}>
                                Match {fixture.match_number}
                              </span>
                            </div>

                            <div className="match-players">
                              <div
                                className="player-card"
                                style={{
                                  display: 'flex',
                                  alignItems: 'center',
                                  justifyContent: 'space-between',
                                  padding: '0.75rem',
                                  borderRadius: '8px',
                                  background: fixture.winner?.id === fixture.player1?.id ? 'var(--accent-secondary)' : 'var(--bg-accent)',
                                  color: fixture.winner?.id === fixture.player1?.id ? 'white' : 'var(--text-primary)',
                                  marginBottom: '0.5rem'
                                }}
                              >
                                <span>{fixture.player1?.name || 'TBD'}</span>
                                {fixture.winner?.id === fixture.player1?.id && <span>üèÜ</span>}
                              </div>

                              <div style={{ textAlign: 'center', margin: '0.5rem 0', color: 'var(--text-muted)' }}>
                                VS
                              </div>

                              <div
                                className="player-card"
                                style={{
                                  display: 'flex',
                                  alignItems: 'center',
                                  justifyContent: 'space-between',
                                  padding: '0.75rem',
                                  borderRadius: '8px',
                                  background: fixture.winner?.id === fixture.player2?.id ? 'var(--accent-secondary)' : 'var(--bg-accent)',
                                  color: fixture.winner?.id === fixture.player2?.id ? 'white' : 'var(--text-primary)'
                                }}
                              >
                                <span>{fixture.player2?.name || 'TBD'}</span>
                                {fixture.winner?.id === fixture.player2?.id && <span>üèÜ</span>}
                              </div>
                            </div>

                            {fixture.winner && (
                              <div className="text-center mt-3">
                                <small className="text-muted">Winner: {fixture.winner.name}</small>
                              </div>
                            )}
                          </div>
                        </div>
                      ))}
                    </div>
                  </div>
                ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Fixtures;
