import React, { useEffect, useState } from 'react';
import api from '../services/api';

type Match = {
	id: number;
	fixture: any;
	status: string;
};

const LiveScore: React.FC = () => {
	const [matches, setMatches] = useState<Match[]>([]);
	const [selected, setSelected] = useState<Match | null>(null);
	const [p1Score, setP1Score] = useState(0);
	const [p2Score, setP2Score] = useState(0);

	useEffect(() => {
		const fetch = async () => {
			try {
				const res = await api.get('/matches/');
				setMatches(res.data);
			} catch (err) {
				console.error(err);
			}
		};
		fetch();
	}, []);

	const startLive = async (m: Match) => {
		try {
			await api.patch(`/matches/${m.id}/`, { status: 'LIVE' });
			setSelected({ ...m, status: 'LIVE' });
		} catch (err) {
			console.error(err);
		}
	};

	const addPoint = (forPlayer: 'p1' | 'p2') => {
		if (!selected) return;
		if (forPlayer === 'p1') setP1Score(s => s + 1);
		else setP2Score(s => s + 1);
	};

	const finishMatch = async () => {
		if (!selected) return;
		const winner = p1Score > p2Score ? selected.fixture.player1 : selected.fixture.player2;
		try {
			await api.patch(`/matches/${selected.id}/`, { status: 'COMPLETED', winner: winner?.id || null });
			alert(`Match completed. Winner: ${winner?.name || 'TBD'}`);
			setSelected(null);
			setP1Score(0);
			setP2Score(0);
		} catch (err) {
			console.error(err);
		}
	};

	return (
		<div style={{ padding: 16 }}>
			<h2>Live Score</h2>
			<div style={{ display: 'flex', gap: 24 }}>
				<div style={{ minWidth: 320 }}>
					<h3>Matches</h3>
					<ul>
						{matches.map(m => (
							<li key={m.id} style={{ marginBottom: 8 }}>
								{`${m.fixture?.player1?.name || 'TBD'} vs ${m.fixture?.player2?.name || 'TBD'}`} â€” {m.status}
								<div>
									<button onClick={() => startLive(m)} style={{ marginTop: 4 }}>Start Live</button>
									<button onClick={() => setSelected(m)} style={{ marginLeft: 8 }}>Open</button>
								</div>
							</li>
						))}
					</ul>
				</div>
				<div style={{ flex: 1 }}>
					{selected ? (
						<div>
							<h3>{`${selected.fixture?.player1?.name || 'TBD'} vs ${selected.fixture?.player2?.name || 'TBD'}`}</h3>
							<div style={{ display: 'flex', gap: 16 }}>
								<div>
									<h4>{selected.fixture?.player1?.name || 'Player 1'}</h4>
									<div style={{ fontSize: 24 }}>{p1Score}</div>
									<button onClick={() => addPoint('p1')}>+1 Point</button>
								</div>
								<div>
									<h4>{selected.fixture?.player2?.name || 'Player 2'}</h4>
									<div style={{ fontSize: 24 }}>{p2Score}</div>
									<button onClick={() => addPoint('p2')}>+1 Point</button>
								</div>
							</div>
							<div style={{ marginTop: 12 }}>
								<button onClick={finishMatch}>Finish Match</button>
								<button onClick={() => { setSelected(null); setP1Score(0); setP2Score(0); }} style={{ marginLeft: 8 }}>Close</button>
							</div>
						</div>
					) : (
						<p>Select a match to open the live score.</p>
					)}
				</div>
			</div>
		</div>
	);
};

export default LiveScore;
