import axios from 'axios';

const API_BASE = (process.env.REACT_APP_API_BASE_URL || 'http://localhost:8000').replace(/\/+$/, '') + '/api';

const api = axios.create({
	baseURL: API_BASE,
	headers: { 'Content-Type': 'application/json' },
});

export default api;
