import { useState } from 'react';
import api from '../services/api';
import styles from './Registration.module.css';

interface PlayerData {
  name: string;
  age: number;
  email?: string;
  phone?: string;
  country?: string;
  category: string;
}

const Registration = () => {
  const [formData, setFormData] = useState({
    name: '',
    age: '',
    email: '',
    phone: '',
    country: '',
    category: 'MS'
  });
  const [message, setMessage] = useState('');
  const [messageType, setMessageType] = useState<'success' | 'error'>('success');
  const [loading, setLoading] = useState(false);

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const submit = async (e: React.FormEvent) => {
    e.preventDefault();
    setLoading(true);
    setMessage('');

    try {
      if (!formData.name.trim()) {
        setMessage('Name is required');
        setMessageType('error');
        setLoading(false);
        return;
      }

      if (!formData.age) {
        setMessage('Age is required');
        setMessageType('error');
        setLoading(false);
        return;
      }

      // Build payload with required fields first
      // Basic validations
      if (!formData.name.trim()) {
        throw new Error('Name is required');
      }
      if (!formData.age || parseInt(formData.age) < 8 || parseInt(formData.age) > 100) {
        throw new Error('Age must be between 8 and 100');
      }

      // Build required fields
      const payload: Record<string, any> = {
        name: formData.name.trim(),
        age: parseInt(formData.age),
        category: formData.category
      };

      // Add optional fields only if they have values
      const email = formData.email.trim();
      const phone = formData.phone.trim();
      const country = formData.country.trim();

      if (email) {
        // Basic email format validation
        if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email)) {
          throw new Error('Invalid email format');
        }
        payload.email = email;
      }
      if (phone) payload.phone = phone;
      if (country) payload.country = country;

      // Only add optional fields if they have non-empty values
      const emailTrimmed = formData.email.trim();
      const phoneTrimmed = formData.phone.trim();
      const countryTrimmed = formData.country.trim();

      if (emailTrimmed) payload.email = emailTrimmed;
      if (phoneTrimmed) payload.phone = phoneTrimmed;
      if (countryTrimmed) payload.country = countryTrimmed;

      const res = await api.post('/players/', payload);
      console.log('Created player:', res.data);
      setMessage(`Player ${res.data.name} registered successfully!`);
      setMessageType('success');

      setFormData({
        name: '',
        age: '',
        email: '',
        phone: '',
        country: '',
        category: 'MS'
      });

    } catch (err: any) {
      console.error('Registration error:', err);
      const detail = err?.response?.data || err?.message || 'Network Error';
      setMessage(typeof detail === 'string' ? detail : JSON.stringify(detail));
      setMessageType('error');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="p-4 fade-in">
      <div className={`card ${styles.registrationCard}`}>
        <div className="card-header">
          <h2 className="mb-0 text-center">üìù Player Registration</h2>
          <p className="text-center text-muted mb-0 mt-2">
            Register new players for the tournament
          </p>
        </div>

        <div className="card-body">
          <form onSubmit={submit}>
            <div className={`grid grid-cols-2 ${styles.formGrid}`}>
              <div className="form-group">
                <label htmlFor="name" className="form-label">
                  Full Name <span className={styles.required}>*</span>
                </label>
                <input
                  id="name"
                  type="text"
                  name="name"
                  value={formData.name}
                  onChange={handleInputChange}
                  required
                  className="form-input"
                  placeholder="Enter full name"
                />
              </div>

              <div className="form-group">
                <label htmlFor="age" className="form-label">
                  Age <span className={styles.required}>*</span>
                </label>
                <input
                  id="age"
                  type="number"
                  name="age"
                  value={formData.age}
                  onChange={handleInputChange}
                  min="8"
                  max="100"
                  required
                  className="form-input"
                  placeholder="Enter age"
                />
              </div>

              <div className="form-group">
                <label htmlFor="email" className="form-label">Email</label>
                <input
                  id="email"
                  type="email"
                  name="email"
                  value={formData.email}
                  onChange={handleInputChange}
                  className="form-input"
                  placeholder="Enter email (optional)"
                />
              </div>

              <div className="form-group">
                <label htmlFor="phone" className="form-label">Phone</label>
                <input
                  id="phone"
                  type="tel"
                  name="phone"
                  value={formData.phone}
                  onChange={handleInputChange}
                  className="form-input"
                  placeholder="Enter phone (optional)"
                />
              </div>

              <div className="form-group">
                <label htmlFor="country" className="form-label">Country</label>
                <input
                  id="country"
                  type="text"
                  name="country"
                  value={formData.country}
                  onChange={handleInputChange}
                  className="form-input"
                  placeholder="Enter country (optional)"
                />
              </div>

              <div className="form-group">
                <label className="form-label">
                  Category <span className={styles.required}>*</span>
                </label>
                <select
                  name="category"
                  value={formData.category}
                  onChange={handleInputChange}
                  className={`form-select ${styles.selectField}`}
                  aria-label="Select Tournament Category"
                >
                  <option value="MS">üèì Men Singles</option>
                  <option value="WS">üèì Women Singles</option>
                  <option value="MD">üèì Men Doubles</option>
                  <option value="WD">üèì Women Doubles</option>
                  <option value="XD">üèì Mixed Doubles</option>
                </select>
              </div>
            </div>

            <div className="text-center mt-4">
              <button
                type="submit"
                disabled={loading}
                className={`btn ${styles.submitButton}`}
              >
                {loading ? '‚è≥ Registering...' : 'üéæ Register Player'}
              </button>
            </div>

            {message && (
              <div className={messageType === 'success' ? `success ${styles.messageContainer}` : `error ${styles.messageContainer}`}>
                <span className={styles.messageIcon}>
                  {messageType === 'success' ? '‚úÖ' : '‚ùå'}
                </span>
                {message}
              </div>
            )}
          </form>
        </div>
      </div>
    </div>
  );
};

export default Registration;
