import React, { useEffect, useState } from 'react';
import api from '../services/api';

const Fixtures: React.FC = () => {
  const [fixtures, setFixtures] = useState<any[]>([]);
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');

  useEffect(() => {
    const fetch = async () => {
      try {
        const res = await api.get('/fixtures/');
        setFixtures(res.data);
      } catch (err) {
        console.error(err);
      }
    };
    fetch();
  }, []);

  const generateDefault = async () => {
    setLoading(true);
    setMessage('Generating fixtures...');
    try {
      const res = await api.post('/fixtures/generate_default/');
      setMessage('Fixtures generated for tournament ' + res.data.tournament_id);
      // refresh fixtures list
      const list = await api.get('/fixtures/fixtures/');
      setFixtures(list.data);
    } catch (err: any) {
      console.error(err);
      setMessage(err?.response?.data ? JSON.stringify(err.response.data) : 'Failed to generate fixtures');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ padding: 16 }}>
      <h2>Fixtures / Bracket</h2>
      <div style={{ marginBottom: 12 }}>
        <button onClick={generateDefault} disabled={loading}>Generate fixtures (auto)</button>
        {message && <span style={{ marginLeft: 8 }}>{message}</span>}
      </div>
      {fixtures.length === 0 && <p>No fixtures yet â€” use the backend fixture generator.</p>}
      <ul>
        {fixtures.map((f, i) => (
          <li key={i}>{`Round ${f.round_number} - Match ${f.match_number}: ${f.player1?.name || 'TBD'} vs ${f.player2?.name || 'TBD'}`}</li>
        ))}
      </ul>
    </div>
  );
};

export default Fixtures;
