import React, { useEffect, useState } from 'react';
import api from '../services/api';

type Match = {
	id: number;
	fixture: any;
	status: string;
};

type Game = {
	id: number;
	game_number: number;
	player1_score: number;
	player2_score: number;
	completed: boolean;
	winner: any;
};

const LiveScore: React.FC = () => {
	const [matches, setMatches] = useState<Match[]>([]);
	const [selectedMatch, setSelectedMatch] = useState<Match | null>(null);
	const [games, setGames] = useState<Game[]>([]);
	const [currentGame, setCurrentGame] = useState<Game | null>(null);

	useEffect(() => {
		fetchMatches();
	}, []);

	const fetchMatches = async () => {
		try {
			const res = await api.get('/matches/');
			setMatches(res.data);
		} catch (err) {
			console.error(err);
		}
	};

	const startLive = async (match: Match) => {
		try {
			await api.post(`/matches/${match.id}/update_status/`, { status: 'LIVE' });
			setSelectedMatch({ ...match, status: 'LIVE' });
			await fetchGames(match.id);
		} catch (err) {
			console.error(err);
		}
	};

	const fetchGames = async (matchId: number) => {
		try {
			const res = await api.get(`/scoring/games/?match=${matchId}`);
			setGames(res.data);
			if (res.data.length > 0) {
				const activeGame = res.data.find((g: Game) => !g.completed);
				setCurrentGame(activeGame || null);
			}
		} catch (err) {
			console.error(err);
		}
	};

	const createNewGame = async () => {
		if (!selectedMatch) return;
		try {
			const res = await api.post('/scoring/games/', {
				match: selectedMatch.id,
				game_number: games.length + 1,
				player1_score: 0,
				player2_score: 0,
				completed: false
			});
			setGames([...games, res.data]);
			setCurrentGame(res.data);
		} catch (err) {
			console.error(err);
		}
	};

	const addPoint = async (playerId: number) => {
		if (!currentGame) return;
		try {
			await api.post(`/scoring/games/${currentGame.id}/add_point/`, { player: playerId });
			await fetchGames(selectedMatch!.id);
		} catch (err) {
			console.error(err);
		}
	};

	const finishMatch = async () => {
		if (!selectedMatch) return;
		try {
			await api.post(`/matches/${selectedMatch.id}/update_status/`, { status: 'COMPLETED' });
			alert('Match completed!');
			setSelectedMatch(null);
			setGames([]);
			setCurrentGame(null);
			fetchMatches();
		} catch (err) {
			console.error(err);
		}
	};

	return (
		<div style={{
			padding: '20px',
			fontFamily: 'Arial, sans-serif',
			background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
			minHeight: '100vh',
			color: 'white'
		}}>
			<h2 style={{
				textAlign: 'center',
				marginBottom: '30px',
				fontSize: '2.5em',
				textShadow: '2px 2px 4px rgba(0,0,0,0.3)'
			}}>
				üèì Live Badminton Scoring
			</h2>

			<div style={{ display: 'flex', gap: '30px', maxWidth: '1200px', margin: '0 auto' }}>
				{/* Matches List */}
				<div style={{
					flex: '0 0 350px',
					background: 'rgba(255,255,255,0.1)',
					borderRadius: '15px',
					padding: '20px',
					backdropFilter: 'blur(10px)',
					border: '1px solid rgba(255,255,255,0.2)'
				}}>
					<h3 style={{ marginBottom: '20px', color: '#ffd700' }}>Available Matches</h3>
					<div style={{ maxHeight: '400px', overflowY: 'auto' }}>
						{matches.map(m => (
							<div key={m.id} style={{
								background: m.status === 'LIVE' ? 'rgba(255,0,0,0.2)' : 'rgba(255,255,255,0.05)',
								borderRadius: '10px',
								padding: '15px',
								marginBottom: '10px',
								border: m.status === 'LIVE' ? '2px solid #ff4444' : '1px solid rgba(255,255,255,0.1)'
							}}>
								<div style={{ fontWeight: 'bold', marginBottom: '10px' }}>
									{m.fixture?.player1?.name || 'TBD'} vs {m.fixture?.player2?.name || 'TBD'}
								</div>
								<div style={{
									display: 'inline-block',
									padding: '4px 12px',
									borderRadius: '20px',
									fontSize: '0.8em',
									background: m.status === 'LIVE' ? '#ff4444' : '#4CAF50',
									color: 'white'
								}}>
									{m.status}
								</div>
								<div style={{ marginTop: '10px' }}>
									<button
										onClick={() => startLive(m)}
										style={{
											background: '#4CAF50',
											color: 'white',
											border: 'none',
											padding: '8px 16px',
											borderRadius: '5px',
											cursor: 'pointer',
											marginRight: '8px'
										}}
									>
										Start Live
									</button>
									<button
										onClick={() => setSelectedMatch(m)}
										style={{
											background: '#2196F3',
											color: 'white',
											border: 'none',
											padding: '8px 16px',
											borderRadius: '5px',
											cursor: 'pointer'
										}}
									>
										Open
									</button>
								</div>
							</div>
						))}
					</div>
				</div>

				{/* Scoring Panel */}
				<div style={{
					flex: 1,
					background: 'rgba(255,255,255,0.1)',
					borderRadius: '15px',
					padding: '20px',
					backdropFilter: 'blur(10px)',
					border: '1px solid rgba(255,255,255,0.2)'
				}}>
					{selectedMatch ? (
						<div>
							<h3 style={{
								textAlign: 'center',
								marginBottom: '20px',
								color: '#ffd700',
								fontSize: '1.5em'
							}}>
								{selectedMatch.fixture?.player1?.name || 'Player 1'} vs {selectedMatch.fixture?.player2?.name || 'Player 2'}
							</h3>

							{/* Games List */}
							<div style={{ marginBottom: '20px' }}>
								<h4 style={{ color: '#ffd700', marginBottom: '10px' }}>Games</h4>
								<div style={{ display: 'flex', gap: '10px', flexWrap: 'wrap' }}>
									{games.map(game => (
										<div
											key={game.id}
											onClick={() => setCurrentGame(game)}
											style={{
												background: currentGame?.id === game.id ? 'rgba(255,215,0,0.3)' : 'rgba(255,255,255,0.05)',
												borderRadius: '8px',
												padding: '10px',
												cursor: 'pointer',
												border: currentGame?.id === game.id ? '2px solid #ffd700' : '1px solid rgba(255,255,255,0.1)',
												minWidth: '80px',
												textAlign: 'center'
											}}
										>
											<div style={{ fontSize: '0.8em', color: '#ccc' }}>Game {game.game_number}</div>
											<div style={{ fontSize: '1.2em', fontWeight: 'bold' }}>
												{game.player1_score} - {game.player2_score}
											</div>
											{game.completed && (
												<div style={{ fontSize: '0.7em', color: '#4CAF50' }}>‚úì Complete</div>
											)}
										</div>
									))}
									{!currentGame && (
										<button
											onClick={createNewGame}
											style={{
												background: '#FF9800',
												color: 'white',
												border: 'none',
												padding: '10px 20px',
												borderRadius: '8px',
												cursor: 'pointer',
												fontSize: '0.9em'
											}}
										>
											+ New Game
										</button>
									)}
								</div>
							</div>

							{/* Current Game Scoring */}
							{currentGame && !currentGame.completed && (
								<div style={{
									background: 'rgba(0,0,0,0.2)',
									borderRadius: '10px',
									padding: '20px',
									marginBottom: '20px'
								}}>
									<h4 style={{ textAlign: 'center', marginBottom: '20px', color: '#ffd700' }}>
										Game {currentGame.game_number} - Live Scoring
									</h4>
									<div style={{
										display: 'flex',
										justifyContent: 'space-around',
										alignItems: 'center',
										marginBottom: '20px'
									}}>
										<div style={{ textAlign: 'center' }}>
											<h5 style={{ marginBottom: '10px' }}>
												{selectedMatch.fixture?.player1?.name || 'Player 1'}
											</h5>
											<div style={{
												fontSize: '4em',
												fontWeight: 'bold',
												color: '#4CAF50',
												marginBottom: '15px'
											}}>
												{currentGame.player1_score}
											</div>
											<button
												onClick={() => addPoint(selectedMatch.fixture.player1.id)}
												style={{
													background: '#4CAF50',
													color: 'white',
													border: 'none',
													padding: '15px 30px',
													borderRadius: '10px',
													cursor: 'pointer',
													fontSize: '1.2em',
													fontWeight: 'bold',
													boxShadow: '0 4px 8px rgba(0,0,0,0.2)',
													transition: 'transform 0.1s'
												}}
												onMouseDown={(e) => e.currentTarget.style.transform = 'scale(0.95)'}
												onMouseUp={(e) => e.currentTarget.style.transform = 'scale(1)'}
											>
												+ POINT
											</button>
										</div>

										<div style={{
											fontSize: '3em',
											color: 'rgba(255,255,255,0.5)',
											fontWeight: 'bold'
										}}>
											VS
										</div>

										<div style={{ textAlign: 'center' }}>
											<h5 style={{ marginBottom: '10px' }}>
												{selectedMatch.fixture?.player2?.name || 'Player 2'}
											</h5>
											<div style={{
												fontSize: '4em',
												fontWeight: 'bold',
												color: '#2196F3',
												marginBottom: '15px'
											}}>
												{currentGame.player2_score}
											</div>
											<button
												onClick={() => addPoint(selectedMatch.fixture.player2.id)}
												style={{
													background: '#2196F3',
													color: 'white',
													border: 'none',
													padding: '15px 30px',
													borderRadius: '10px',
													cursor: 'pointer',
													fontSize: '1.2em',
													fontWeight: 'bold',
													boxShadow: '0 4px 8px rgba(0,0,0,0.2)',
													transition: 'transform 0.1s'
												}}
												onMouseDown={(e) => e.currentTarget.style.transform = 'scale(0.95)'}
												onMouseUp={(e) => e.currentTarget.style.transform = 'scale(1)'}
											>
												+ POINT
											</button>
										</div>
									</div>
								</div>
							)}

							{/* Control Buttons */}
							<div style={{ textAlign: 'center' }}>
								<button
									onClick={finishMatch}
									style={{
										background: '#f44336',
										color: 'white',
										border: 'none',
										padding: '12px 24px',
										borderRadius: '8px',
										cursor: 'pointer',
										fontSize: '1em',
										marginRight: '10px'
									}}
								>
									Finish Match
								</button>
								<button
									onClick={() => {
										setSelectedMatch(null);
										setGames([]);
										setCurrentGame(null);
									}}
									style={{
										background: '#9E9E9E',
										color: 'white',
										border: 'none',
										padding: '12px 24px',
										borderRadius: '8px',
										cursor: 'pointer',
										fontSize: '1em'
									}}
								>
									Close
								</button>
							</div>
						</div>
					) : (
						<div style={{
							textAlign: 'center',
							padding: '50px',
							color: 'rgba(255,255,255,0.7)'
						}}>
							<div style={{ fontSize: '4em', marginBottom: '20px' }}>üèì</div>
							<p style={{ fontSize: '1.2em' }}>Select a match to start live scoring</p>
						</div>
					)}
				</div>
			</div>
		</div>
	);
};

export default LiveScore;
