import { useEffect, useState } from 'react';
import { Routes, Route, Link, Navigate } from 'react-router-dom';
import Navigation from './components/Navigation';
import Registration from './pages/Registration';
import PlayersList from './pages/PlayersList';
import Fixtures from './pages/Fixtures';
import Schedule from './pages/Schedule';
import LiveScore from './pages/LiveScore';
import Results from './pages/Results';
import AdminPanel from './pages/AdminPanel';
import RefereePanel from './pages/RefereePanel';
import Login from './pages/Login';
import { useAuth } from './contexts/AuthContext';
import api from './services/api';

const App = () => {
  const { isAuthenticated, loading: authLoading } = useAuth();
  const [stats, setStats] = useState({
    totalPlayers: 0,
    totalMatches: 0,
    liveMatches: 0,
    completedMatches: 0,
  });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchStats = async () => {
      try {
        const [playersRes, matchesRes] = await Promise.all([
          api.get('/players/'),
          api.get('/matches/'),
        ]);

        const players = playersRes.data;
        const matches = matchesRes.data;

        setStats({
          totalPlayers: players.length,
          totalMatches: matches.length,
          liveMatches: matches.filter((m: any) => m.status === 'LIVE').length,
          completedMatches: matches.filter((m: any) => m.status === 'COMPLETED').length,
        });
      } catch (error) {
        console.error('Failed to fetch stats:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchStats();
  }, []);

  const HomePage = () => (
    <div className="p-4 fade-in">
      {/* Hero Section */}
      <div className="hero mb-5">
        <div className="hero-content">
          <h1 style={{ marginBottom: '1.5rem', fontSize: 'clamp(2.5rem, 6vw, 4rem)' }}>
            üèì Badminton Tournament Manager
          </h1>
          <p style={{ fontSize: 'clamp(1rem, 2vw, 1.25rem)', marginBottom: '2.5rem', color: 'var(--text-secondary)', maxWidth: '600px', margin: '0 auto 2.5rem' }}>
            Professional tournament management system for badminton enthusiasts. Track players, manage fixtures, and score matches in real-time.
          </p>
          <div style={{ display: 'flex', gap: '1rem', justifyContent: 'center', flexWrap: 'wrap' }}>
            <Link to="/register" className="btn btn-primary">
              üìù Register Player
            </Link>
            <Link to="/live" className="btn btn-secondary">
              üéØ View Live Scores
            </Link>
            <Link to="/fixtures" className="btn btn-outline">
              üèÜ Tournament Fixtures
            </Link>
          </div>
        </div>
      </div>

      {/* Stats Dashboard */}
      <div className="mb-5">
        <h2 className="mb-4 text-center">üìä Tournament Overview</h2>
        <div className="grid grid-auto-fit">
          <div className="stats-card scale-in" style={{ animationDelay: '0.1s' }}>
            <div className="stats-card-icon">üë•</div>
            <div className="stats-card-value">{loading ? '...' : stats.totalPlayers}</div>
            <div className="stats-card-label">Total Players</div>
          </div>

          <div className="stats-card scale-in" style={{ animationDelay: '0.2s' }}>
            <div className="stats-card-icon">üèì</div>
            <div className="stats-card-value">{loading ? '...' : stats.totalMatches}</div>
            <div className="stats-card-label">Total Matches</div>
          </div>

          <div className="stats-card scale-in" style={{ animationDelay: '0.3s' }}>
            <div className="stats-card-icon">
              <span className="live-indicator" style={{ width: '12px', height: '12px' }}></span>
            </div>
            <div className="stats-card-value" style={{ color: 'var(--error)' }}>
              {loading ? '...' : stats.liveMatches}
            </div>
            <div className="stats-card-label">Live Matches</div>
          </div>

          <div className="stats-card scale-in" style={{ animationDelay: '0.4s' }}>
            <div className="stats-card-icon">üèÖ</div>
            <div className="stats-card-value" style={{ color: 'var(--accent-secondary)' }}>
              {loading ? '...' : stats.completedMatches}
            </div>
            <div className="stats-card-label">Completed</div>
          </div>
        </div>
      </div>

      {/* Quick Actions */}
      <div className="card slide-up">
        <div className="card-header">
          <h3 className="mb-0">‚ö° Quick Actions</h3>
        </div>
        <div className="card-body">
          <div className="grid grid-auto-fit">
            <Link to="/fixtures" className="btn btn-secondary">
              üèÜ Generate Fixtures
            </Link>
            <Link to="/schedule" className="btn btn-secondary">
              üìÖ Manage Schedule
            </Link>
            <Link to="/players" className="btn btn-secondary">
              üë• View Players
            </Link>
            <Link to="/results" className="btn btn-secondary">
              üèÖ View Results
            </Link>
          </div>
        </div>
      </div>
    </div>
  );

  return (
    <div className="app">
      <Navigation />
      <Routes>
        <Route path="/" element={<HomePage />} />
        <Route path="/register" element={<Registration />} />
        <Route path="/players" element={<PlayersList />} />
        <Route path="/fixtures" element={<Fixtures />} />
        <Route path="/schedule" element={<Schedule />} />
        <Route path="/live" element={<LiveScore />} />
        <Route path="/results" element={<Results />} />
        <Route path="/admin" element={<AdminPanel />} />
        <Route path="/referee" element={<RefereePanel />} />
      </Routes>
    </div>
  );
};

export default App;
