import React, { useState } from 'react';
import api from '../services/api';

const Registration: React.FC = () => {
  const [name, setName] = useState('');
  const [age, setAge] = useState<number | ''>('');
  const [category, setCategory] = useState('MS');
  const [message, setMessage] = useState('');

  const submit = async (e: React.FormEvent) => {
    e.preventDefault();
    try {
      const payload = { name, age: typeof age === 'number' ? age : parseInt(String(age), 10), category };
      const res = await api.post('/players/', payload);
      setMessage('Player registered successfully');
      setName('');
      setAge('');
      // optionally redirect or refresh list
      console.log('created player', res.data);
    } catch (err: any) {
      console.error(err);
      const detail = err?.response?.data || err?.message || 'Failed to register player';
      // present either serializer errors or a simple message
      setMessage(typeof detail === 'string' ? detail : JSON.stringify(detail));
    }
  };

  return (
    <div className="pt-3">
      <h2>Player Registration</h2>
      <form onSubmit={submit}>
        <div className="form-group">
          <label>Name</label>
          <input className="form-control" value={name} onChange={e => setName(e.target.value)} required />
        </div>
        <div className="form-group">
          <label>Age</label>
          <input className="form-control" type="number" value={age as any} onChange={e => setAge(e.target.value === '' ? '' : parseInt(e.target.value))} required />
        </div>
        <div className="form-group">
          <label>Category</label>
          <select className="form-control" value={category} onChange={e => setCategory(e.target.value)}>
            <option value="MS">Men Singles</option>
            <option value="WS">Women Singles</option>
            <option value="MD">Men Doubles</option>
            <option value="WD">Women Doubles</option>
            <option value="XD">Mixed Doubles</option>
          </select>
        </div>
        <div className="mt-3">
          <button className="btn btn-primary" type="submit">Register</button>
        </div>
      </form>
      {message && <p className="mt-3">{message}</p>}
    </div>
  );
};

export default Registration;
