import React, { useEffect, useState } from 'react';
import api from '../services/api';

interface Match {
	id: number;
	fixture: {
		id: number;
		player1: { id: number; name: string } | null;
		player2: { id: number; name: string } | null;
		round_number: number;
		match_number: number;
	};
	court_number: number | null;
	scheduled_time: string;
	status: string;
	winner: { id: number; name: string } | null;
}

const Schedule: React.FC = () => {
	const [matches, setMatches] = useState<Match[]>([]);
	const [loading, setLoading] = useState(true);
	const [error, setError] = useState('');
	const [filter, setFilter] = useState<'all' | 'scheduled' | 'live' | 'completed'>('all');
	const [sortBy, setSortBy] = useState<'time' | 'court'>('time');

	useEffect(() => {
		fetchMatches();
	}, []);

	const fetchMatches = async () => {
		try {
			const res = await api.get('/matches/');
			setMatches(res.data);
			setError('');
		} catch (err) {
			console.error('Failed to fetch matches', err);
			setError('Failed to load match schedule');
		} finally {
			setLoading(false);
		}
	};

	const updateMatch = async (id: number, patch: Partial<Match>) => {
		try {
			const res = await api.patch(`/matches/${id}/`, patch);
			setMatches(prev => prev.map(m => (m.id === id ? res.data : m)));
		} catch (err) {
			console.error('Failed to update match', err);
			alert('Failed to update match. Please try again.');
		}
	};

	const filteredMatches = matches.filter(match => {
		switch (filter) {
			case 'scheduled': return match.status === 'SCHEDULED';
			case 'live': return match.status === 'LIVE';
			case 'completed': return match.status === 'COMPLETED';
			default: return true;
		}
	});

	const sortedMatches = [...filteredMatches].sort((a, b) => {
		if (sortBy === 'time') {
			return new Date(a.scheduled_time).getTime() - new Date(b.scheduled_time).getTime();
		} else {
			return (a.court_number || 999) - (b.court_number || 999);
		}
	});

	const getStatusBadge = (status: string) => {
		const statusConfig = {
			SCHEDULED: { bg: 'var(--bg-accent)', color: 'var(--text-primary)', text: 'Scheduled' },
			LIVE: { bg: '#ef4444', color: 'white', text: 'Live' },
			COMPLETED: { bg: 'var(--accent-secondary)', color: 'white', text: 'Completed' },
		};
		const config = statusConfig[status as keyof typeof statusConfig] || statusConfig.SCHEDULED;
		return (
			<span style={{
				background: config.bg,
				color: config.color,
				padding: '0.25rem 0.75rem',
				borderRadius: '12px',
				fontSize: '0.8rem',
				fontWeight: '500'
			}}>
				{config.text}
			</span>
		);
	};

	const assignCourt = (matchId: number) => {
		const court = prompt('Assign court number (1-10):');
		if (court) {
			const courtNum = parseInt(court, 10);
			if (courtNum >= 1 && courtNum <= 10) {
				updateMatch(matchId, { court_number: courtNum });
			} else {
				alert('Please enter a valid court number (1-10)');
			}
		}
	};

	const rescheduleMatch = (matchId: number) => {
		const newTime = prompt('Enter new scheduled time (YYYY-MM-DD HH:MM):');
		if (newTime) {
			updateMatch(matchId, { scheduled_time: newTime });
		}
	};

	return (
		<div className="p-4 fade-in">
			<div className="card">
				<div className="card-header">
					<div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', flexWrap: 'wrap', gap: '1rem' }}>
						<h2 className="mb-0">üìÖ Match Schedule</h2>
						<div style={{ display: 'flex', gap: '0.5rem', flexWrap: 'wrap' }}>
						</div>
					</div>
				</div>
				<div className="card-body">
					{error && <div className="error">{error}</div>}

					{loading ? (
						<div className="loading">
							<div>Loading schedule...</div>
						</div>
					) : matches.length === 0 ? (
						<div className="text-center p-4">
							<div style={{ fontSize: '4rem', marginBottom: '1rem' }}>üìÖ</div>
							<h3>No matches scheduled</h3>
							<p className="text-muted">Generate fixtures first to create match schedules</p>
						</div>
					) : (
						<div className="table-responsive">
							<table className="table">
								<thead>
									<tr>
										<th>Court</th>
										<th>Time</th>
										<th>Round</th>
										<th>Match</th>
										<th>Players</th>
										<th>Status</th>
										<th>Actions</th>
									</tr>
								</thead>
								<tbody>
									{sortedMatches.map((match) => (
										<tr key={match.id}>
											<td>
												{match.court_number ? (
													<span className="badge" style={{
														background: 'var(--accent-primary)',
														color: 'white',
														padding: '0.25rem 0.5rem',
														borderRadius: '12px'
													}}>
														Court {match.court_number}
													</span>
												) : (
													<span className="text-muted">Unassigned</span>
												)}
											</td>
											<td>
												<div style={{ fontSize: '0.9rem' }}>
													<div style={{ fontWeight: '500' }}>
														{new Date(match.scheduled_time).toLocaleDateString()}
													</div>
													<div className="text-muted">
														{new Date(match.scheduled_time).toLocaleTimeString([], {
															hour: '2-digit',
															minute: '2-digit'
														})}
													</div>
												</div>
											</td>
											<td>
												<span className="badge" style={{
													background: 'var(--accent-secondary)',
													color: 'white',
													padding: '0.25rem 0.5rem',
													borderRadius: '12px'
												}}>
													Round {match.fixture.round_number}
												</span>
											</td>
											<td>
												<span className="text-muted">#{match.fixture.match_number}</span>
											</td>
											<td>
												<div style={{ display: 'flex', flexDirection: 'column', gap: '0.25rem' }}>
													<span>{match.fixture.player1?.name || 'TBD'}</span>
													<span style={{ color: 'var(--text-muted)' }}>vs</span>
													<span>{match.fixture.player2?.name || 'TBD'}</span>
												</div>
											</td>
											<td>
												{getStatusBadge(match.status)}
											</td>
											<td>
												<div style={{ display: 'flex', gap: '0.25rem', flexWrap: 'wrap' }}>
													{match.status === 'SCHEDULED' && (
														<button
															onClick={() => updateMatch(match.id, { status: 'LIVE' })}
															className="btn btn-secondary"
															style={{ fontSize: '0.7rem', padding: '0.25rem 0.5rem' }}
														>
															üéØ Start
														</button>
													)}
													{match.status === 'LIVE' && (
														<button
															onClick={() => updateMatch(match.id, { status: 'COMPLETED' })}
															className="btn"
															style={{ fontSize: '0.7rem', padding: '0.25rem 0.5rem', background: 'var(--accent-secondary)' }}
														>
															üèÅ Finish
														</button>
													)}
													<button
														onClick={() => assignCourt(match.id)}
														className="btn btn-outline"
														style={{ fontSize: '0.7rem', padding: '0.25rem 0.5rem' }}
													>
														üèì Court
													</button>
													<button
														onClick={() => rescheduleMatch(match.id)}
														className="btn btn-outline"
														style={{ fontSize: '0.7rem', padding: '0.25rem 0.5rem' }}
													>
														üìÖ Time
													</button>
												</div>
											</td>
										</tr>
									))}
								</tbody>
							</table>
						</div>
					)}

					{matches.length > 0 && (
						<div className="mt-4 p-3" style={{
							background: 'var(--bg-accent)',
							borderRadius: 'var(--border-radius-md)',
							textAlign: 'center'
						}}>
							<h4 className="mb-2 court">Schedule Summary</h4>
							<div style={{ display: 'flex', justifyContent: 'center', gap: '2rem', flexWrap: 'wrap' }}>
								<div>
									<div style={{ fontSize: '1.5rem', fontWeight: 'bold' }}>{matches.length}</div>
									<div className="text-muted">Total Matches</div>
								</div>
								<div>
									<div style={{ fontSize: '1.5rem', fontWeight: 'bold' }}>
										{matches.filter(m => m.status === 'LIVE').length}
									</div>
									<div className="text-muted">Live Matches</div>
								</div>
								<div>
									<div style={{ fontSize: '1.5rem', fontWeight: 'bold' }}>
										{matches.filter(m => m.court_number).length}
									</div>
									<div className="text-muted">Assigned Courts</div>
								</div>
							</div>
						</div>
					)}
				</div>
			</div>
		</div>
	);
};

export default Schedule;
