import React, { useEffect, useState } from 'react';
import api from '../services/api';

const Fixtures: React.FC = () => {
  const [fixtures, setFixtures] = useState<any[]>([]);

  useEffect(() => {
    const fetch = async () => {
      try {
        const res = await api.get('/fixtures/');
        setFixtures(res.data);
      } catch (err) {
        console.error(err);
      }
    };
    fetch();
  }, []);

  return (
    <div style={{ padding: 16 }}>
      <h2>Fixtures / Bracket</h2>
      {fixtures.length === 0 && <p>No fixtures yet â€” use the backend fixture generator.</p>}
      <ul>
        {fixtures.map((f, i) => (
          <li key={i}>{`Round ${f.round_number} - Match ${f.match_number}: ${f.player1?.name || 'TBD'} vs ${f.player2?.name || 'TBD'}`}</li>
        ))}
      </ul>
    </div>
  );
};

export default Fixtures;
