import React from 'react';
import { Link, useLocation } from 'react-router-dom';

const Navigation: React.FC = () => {
  const location = useLocation();

  const navItems = [
    { path: '/', label: 'üè† Home', icon: 'üè†' },
    { path: '/register', label: 'üìù Register', icon: 'üìù' },
    { path: '/players', label: 'üë• Players', icon: 'üë•' },
    { path: '/fixtures', label: 'üèÜ Fixtures', icon: 'üèÜ' },
    { path: '/schedule', label: 'üìÖ Schedule', icon: 'üìÖ' },
    { path: '/live', label: 'üéØ Live Score', icon: 'üéØ' },
    { path: '/results', label: 'üèÖ Results', icon: 'üèÖ' },
  ];

  return (
    <nav>
      {navItems.map((item) => (
        <Link
          key={item.path}
          to={item.path}
          className={location.pathname === item.path ? 'active' : ''}
        >
          <span style={{ marginRight: '0.5rem' }}>{item.icon}</span>
          {item.label}
        </Link>
      ))}
    </nav>
  );
};

export default Navigation;
